import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getUser } from '../Auth';
import './Pages.css';

export default function User() {
  const [userData, setUserData] = useState(getUser());
  const [openAuctions, setOpenAuctions] = useState([]);
  const [closedAuctions, setClosedAuctions] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({ email: '', phone: '' });

  useEffect(() => {
    fetchUserAuctions();
  }, [userData.id]);

  const fetchUserAuctions = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/users/${userData.id}/auctions`);
      setOpenAuctions(response.data.open);
      setClosedAuctions(response.data.closed);
    } catch (err) {
      console.error('Error loading auctions', err);
    }
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:8080/api/users/${userData.id}`, form);
      alert('הפרטים עודכנו');
      setUserData({ ...userData, ...form });
      setEditMode(false);
    } catch (err) {
      alert('שגיאה בעדכון');
    }
  };

  return (
    <div className="profile-container" dir="rtl">
      <h2>פרופיל משתמש</h2>
      {editMode ? (
        <>
          <input value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} placeholder="אימייל" />
          <input value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} placeholder="טלפון" />
          <button onClick={handleUpdate}>שמור</button>
        </>
      ) : (
        <>
          <p>אימייל: {userData.email}</p>
          <p>טלפון: {userData.phone}</p>
          <button onClick={() => setEditMode(true)}>ערוך פרטים</button>
        </>
      )}

      <h3>מכרזים פתוחים</h3>
      {openAuctions.map(item => (
        <div key={item.id} className="auction-card">
          <p>כותרת: {item.title}</p>
          <p>מחיר אחרון: ₪{item.currentPrice}</p>
        </div>
      ))}

      <h3>מכרזים סגורים</h3>
      {closedAuctions.map(item => (
        <div key={item.id} className="auction-card">
          <p>כותרת: {item.title}</p>
          <p>מחיר סיום: ₪{item.currentPrice}</p>
          <p>זוכה: {item.winner?.username || 'לא נבחר'}</p>
        </div>
      ))}
    </div>
  );
}
