import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Pages.css';

export default function Home() {
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);

   useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/items/active');
        console.log('Fetched items:', response.data); //  Debug 
        setItems(response.data);
      } catch (error) {
        console.error('Failed to load items:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchItems();
  }, []);

  
  const filteredItems = items
  .filter(item => item.status === 'active')
  .filter(item =>
    !search || (item.title && item.title.toLowerCase().includes(search.toLowerCase()))
  )
  .sort((a, b) => new Date(b.startDate) - new Date(a.startDate));
    

  if (loading) {
    return <div className="signin-container">טוען מוצרים...</div>;
  }

  return (
    <div className="home-container p-4" dir="rtl">
      <input
        type="text"
        placeholder="חיפוש חופשי..."
        className="search-input signin-input"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />

      {filteredItems.length === 0 ? (
        <p>לא נמצאו מוצרים פעילים תואמים.</p>
      ) : (
        <div className="item-grid">
          {filteredItems.map(item => (
  <div key={item.id} className="item-card">
    
      <img src={item.imageUrl} alt={item.title} className="item-img" />
      <h3 className="item-title">{item.title}</h3>
      <p className="item-price">מחיר נוכחי: ₪{item.currentPrice}</p>
    
    <button
      onClick={() => window.location.href = `/itemdetails/${item.id}`}
      className="signin-btn"
      style={{ marginTop: '0.5rem' }}
    >
      הגש הצעה
    </button>
  </div>
))}
        </div>
      )}
    </div>
  );
}
