import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Pages.css';

export default function Home() {
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch all items from the backend
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/items/active');
        setItems(response.data);
      } catch (error) {
        console.error('Failed to load items:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchItems();
  }, []);

  // Filter active items and search by title, then sort by newest startDate
  const filteredItems = items
    .filter(item => item.status === 'active')
    .filter(item => item.title.toLowerCase().includes(search.toLowerCase()))
    .sort((a, b) => new Date(b.startDate) - new Date(a.startDate));

  if (loading) {
    return <div className="signin-container">טוען מוצרים...</div>;
  }

  return (
    <div className="home-container p-4" dir="rtl">
      <input
        type="text"
        placeholder="חיפוש חופשי..."
        className="search-input signin-input"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />

      {filteredItems.length === 0 ? (
        <p>לא נמצאו מוצרים פעילים תואמים.</p>
      ) : (
        <div className="item-grid">
          {filteredItems.map(item => (
            <Link to={`/itemdetails/${item.id}`} key={item.id} className="item-card">
              <img src={item.imageUrl} alt={item.title} className="item-img" />
              <h3 className="item-title">{item.title}</h3>
              <p className="item-price">מחיר נוכחי: ₪{item.currentPrice}</p>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}
