import Modal from 'react-modal';
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import AddItem from './pages/AddItem';
import ItemDetails from './pages/ItemDetails';
import ItemOffers from './pages/ItemOffers';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import User from './pages/User';
import PrivateRoute from "./components/PrivateRoute";
import { isLoggedIn } from './Auth';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  useEffect(() => {
    if (!isLoggedIn()) {
      setShowModal(true);
    }
  }, []);

  const handleLoginSuccess = () => {
    setShowModal(false);
    window.location.reload(); // רענון קל שיעדכן את ה־Navbar וכו'
  };

  const handleSwitch = () => {
    setShowSignup(prev => !prev);
  };
Modal.setAppElement('#root');


  return (
    <Router>
      <Navbar />

      {/* Modal for login/signup */}
      <Modal
        isOpen={showModal}
        onRequestClose={() => {}}
        shouldCloseOnOverlayClick={false}
        shouldCloseOnEsc={false}
        contentLabel="Authentication"
        className="Modal"
        overlayClassName="Overlay"
      >
        {showSignup ? (
          <SignUp onSuccess={handleLoginSuccess} />
        ) : (
          <Login onSuccess={handleLoginSuccess} onSwitch={handleSwitch} />
        )}
      </Modal>

      <div className="p-4">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/home" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/additem" element={<AddItem />} />
          <Route path="/itemdetails/:id" element={<ItemDetails />} />
          <Route path="/itemoffers/:id" element={<ItemOffers />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/user" element={<User />} />
        </Routes>
      </div>
    </Router>
  );
}export default App;
