import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div style={styles.container}>
      <h1>Welcome to the Auction Site</h1>
      <p>Bid on exciting items and win great deals!</p>

      <Link to="/auctions">
        <button style={styles.button}>View Auctions</button>
      </Link>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '2rem',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#1e90ff',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
  },
};

export default HomePage;
