import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Pages.css';

export default function ItemOffers() {
  const { id } = useParams(); // id = item id
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOffers = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/offers/item/${id}`);
        setOffers(response.data);
      } catch (error) {
        console.error('Error fetching offers:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOffers();
  }, [id]);

  if (loading) return <div className="signin-container">טוען הצעות...</div>;

  return (
    <div className="signin-container" dir="rtl">
      <h2 className="signin-title">הצעות על פריט #{id}</h2>
      {offers.length === 0 ? (
        <p>לא נמצאו הצעות עבור פריט זה.</p>
      ) : (
        <table className="offers-table">
          <thead>
            <tr>
              <th>סכום ההצעה</th>
              <th>תאריך</th>
              <th>מגיש ההצעה</th>
            </tr>
          </thead>
          <tbody>
            {offers.map((offer) => (
              <tr key={offer.id}>
                <td>₪{offer.amount}</td>
                <td>{new Date(offer.offerTime).toLocaleString('he-IL')}</td>
                <td>{offer.bidder?.email || 'לא ידוע'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
