import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ItemDetails.css'; // Optional: for cleaner styling

const ItemDetails = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const res = await axios.get(`/api/items/${id}`);
        setItem(res.data);
      } catch (error) {
        console.error('Error fetching item:', error);
      }
    };

    fetchItem();
  }, [id]);

  if (!item) return <div>טוען פריט...</div>;

  return (
    <div className="item-details-container">
      {/* Top navbar can be a shared component */}
      <nav className="navbar">
        <button onClick={() => window.location.href = '/'}>בית</button>
        <button>פרופיל</button>
        <button>התנתק</button>
      </nav>

      <h2>צפייה בפריט</h2>

      <div className="item-box">
        <div className="item-main">
          <img src={item.imageUrl || '/placeholder.jpg'} alt="item" className="item-image" />
          <p>{item.description}</p>
        </div>

        <div className="item-details">
          <p><strong>תאריך התחלה:</strong> {item.startDate}</p>
          <p><strong>תאריך סיום:</strong> {item.endDate}</p>
          <p><strong>מחיר התחלה:</strong> {item.startingPrice} ₪</p>
          <p><strong>מחיר מוצע נוכחי:</strong> {item.highestBid ?? 'לא הוגשה הצעה'}</p>

          <button className="offer-button">הצעת מחיר חדשה</button>
          <button className="view-offers-button">צפייה בהצעות</button>
        </div>
      </div>

      <p className="owner-hint">למי שהפריט בחשבון שלו → אפשרות לנהל הצעות</p>
    </div>
  );
};

export default ItemDetails;
