import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { getUser } from '../Auth';
import './Pages.css';

export default function User() {
  
  const [userData]  = useState(getUser())
  const [openAuctions, setOpenAuctions] = useState([]);
  const [closedAuctions, setClosedAuctions] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [password, setPassword] = useState('');

  const fetchUserAuctions = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/users/${userData.id}/auctions`);
      setOpenAuctions(response.data.open);
      setClosedAuctions(response.data.closed);
    } catch (err) {
      console.error('Error loading auctions', err);
    }
  }, [userData.id]);

  useEffect(() => {
    fetchUserAuctions();
  }, [fetchUserAuctions]);

  const handleUpdate = async () => {
    if (!password.trim()) {
      alert('הסיסמה לא יכולה להיות ריקה');
      return;
    }
    try {
      await axios.put(`http://localhost:8080/api/users/${userData.id}`, { password });
      alert('הסיסמה עודכנה');
      setEditMode(false);
    } catch (err) {
      alert('שגיאה בעדכון');
    }
  };

  return (
    <div className="profile-container" dir="rtl">
      <h2>פרופיל משתמש</h2>
      <p>שם משתמש: {userData.username}</p>
      <p>טלפון: {userData.phone}</p>

      {editMode ? (
        <>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="סיסמה חדשה"
          />
          <button onClick={handleUpdate}>שמור</button>
        </>
      ) : (
        <button onClick={() => setEditMode(true)}>שנה סיסמה</button>
      )}

      <h3>מכרזים פתוחים</h3>
      <div className="auction-grid">
        {openAuctions.map(item => (
          <div key={item.id} className="auction-card">
            <h4>{item.title}</h4>
            <p>מחיר נוכחי: ₪{item.currentPrice}</p>
          </div>
        ))}
      </div>

      <h3>מכרזים סגורים</h3>
      <div className="auction-grid">
        {closedAuctions.map(item => (
          <div key={item.id} className="auction-card">
            <h4>{item.title}</h4>
            <p>מחיר סיום: ₪{item.currentPrice}</p>
            <p>זוכה: {item.winner?.username || 'לא נבחר'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
