import { getUser } from "../Auth";
import { isLoggedIn } from "../Auth";
import React, { useState } from 'react';
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './Pages.css';
import axios from 'axios';

function AddItem() {
    const user = getUser();
console.log("Logged in user:", user);

const navigate = useNavigate();

useEffect(() => {
  if (!isLoggedIn()) {
    navigate("/login");
  }
}, [navigate]);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    imageUrl: '',
    startPrice: '',
    startDate: '',
    endDate: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const adjustedData = {
      ...formData,
      startingPrice: parseFloat(formData.startingPrice),
      startDate: formData.startDate + 'T00:00:00',
      endDate: formData.endDate + 'T00:00:00',
      seller: { id: user.id } 
    };
console.log("Sending to backend:", adjustedData);
    try {
      await axios.post('http://localhost:8080/api/items', adjustedData);
      alert('Item added successfully!');
      setFormData({
        title: '',
        description: '',
        imageUrl: '',
        startingPrice: '',
        startDate: '',
        endDate: ''
      });
    } catch (error) {
      console.error('Error adding item:', error);
      alert('Failed to add item');
    }
  };

  return (
    <div className="signin-container" dir="rtl">
      <h2 className="signin-title">הוספת מוצר</h2>
      <form className="signin-form" onSubmit={handleSubmit}>
        <input
          name="title"
          type="text"
          placeholder="כותרת"
          className="signin-input"
          value={formData.title}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="תיאור"
          className="signin-input"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <input
          name="imageUrl"
          type="text"
          placeholder="קישור לתמונה"
          className="signin-input"
          value={formData.imageUrl}
          onChange={handleChange}
          required
        />
        <input
          name="startingPrice"
          type="number"
          placeholder="מחיר התחלתי"
          className="signin-input"
          value={formData.startPrice}
          onChange={handleChange}
          required
        />
        <label className="signin-label">תאריך התחלה</label>
        <input
          name="startDate"
          type="date"
          className="signin-input"
          value={formData.startDate}
          onChange={handleChange}
          required
        />
         <label className="signin-label">תאריך סיום</label>
        <input
          name="endDate"
          type="date"
          className="signin-input"
          value={formData.endDate}
          onChange={handleChange}
          required
        />
        <div className="signin-buttons">
          <button type="submit" className="signin-btn">הוסף מוצר</button>
        </div>
      </form>
    </div>
  );
}

export default AddItem;
