import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getUser } from '../Auth';
import './Pages.css';

export default function ItemDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = getUser();
  const [item, setItem] = useState(null);
  const [offerAmount, setOfferAmount] = useState('');
 
  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/items/${id}`);
        setItem(response.data);
      } catch (error) {
        console.error('Failed to fetch item:', error);
      }
    };
    fetchItem();
  }, [id]);

  const handleOfferSubmit = async () => {
    try {
      await axios.post('http://localhost:8080/api/offers', {
        amount: parseFloat(offerAmount),
        itemId: item.id,
        bidderId: user.id,
      });
      alert('Offer submitted!');
      setOfferAmount('');
    } catch (error) {
      alert('Failed to submit offer');
      console.error(error);
    }
  };

  if (!item) return <div>Loading...</div>;

  return (
    <div className="item-details-container" dir="rtl">
      <h2>{item.title}</h2>
      <img src={item.imageUrl} alt={item.title} className="item-img" />
      <p>{item.description}</p>
      <p>תאריך התחלה: {new Date(item.startDate).toLocaleDateString('he-IL')}</p>
      <p>תאריך סיום: {new Date(item.endDate).toLocaleDateString('he-IL')}</p>
      <p>מחיר התחלתי: ₪{item.startingPrice}</p>
      <p>מחיר נוכחי: ₪{item.currentPrice}</p>

      {user?.id !== item.seller?.id ? (
        <>
          <label>סכום הצעה:</label>
          <input
            type="number"
            value={offerAmount}
            onChange={(e) => setOfferAmount(e.target.value)}
            className="signin-input"
          />
          <button onClick={handleOfferSubmit} className="signin-btn">הגש הצעה</button>
        </>
      ) : (
        <button onClick={() => navigate(`/offers/${item.id}`)} className="signin-btn">
          הצג הצעות
        </button>
      )}
    </div>
  );
}
