import './Pages.css';
import React, { useState } from 'react';
import axios from 'axios';
function Login() {
     const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

 const handleLogin = async () => {
  try {
    const response = await axios.post('http://localhost:8080/login', {
      username: 'yourUsername',
      password: 'yourPassword'
    });
localStorage.setItem('isAuthenticated', 'true');
    console.log('Login success:', response.data);
  } catch (error) {
    if (error.response) {
      console.error('Login failed:', error.response.status, error.response.data);
    } else {
      console.error('Error:', error.message);
    }
  }
};
  return (
    <div className="signin-container" dir="rtl">
      <h2 className="signin-title">כניסה לחשבון</h2>
      <form className="signin-form" onSubmit={handleLogin}>
       <input className="signin-input" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
      <input className="signin-input" value={password} type="password" onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
        <div className="signin-buttons">
          <button type="submit" className="signin-btn">הכנס</button>
          <button type="button" className="signin-btn">הרשם</button>
        </div>
      </form>
    </div>
  );
}

export default Login;
