import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
const Home = () => {
  const [search, setSearch] = useState('');
  const [items, setItems] = useState([]);
const navigate = useNavigate();

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get('/api/items'); // Adjust to your backend endpoint
      setItems(response.data);
    } catch (err) {
      console.error('Failed to fetch items:', err);
    }
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
    // You can also add logic here to filter/search items on change
  };

  const handleOffer = (itemId) => {
    alert(`הצעה נשלחה עבור מוצר ${itemId}`);
    // You can implement sending an offer via modal/API call here
  };

  return (
    <div style={styles.page} dir="rtl">
      <div style={styles.searchContainer}>
        <input
          type="text"
          placeholder="חיפוש לפי שם / קטגוריה"
          value={search}
          onChange={handleSearch}
          style={styles.searchInput}
        />
      </div>

      <div style={styles.grid}>
        {items.map((item) => (
          <div key={item.id} style={styles.card} onClick={() => navigate(`/item/${item.id}`)} >
            <img src={item.imageUrl || '/placeholder.jpg'} alt="item" style={styles.image} />
            <div style={styles.description}>{item.description}</div>
            <button style={styles.button} onClick={() => handleOffer(item.id)}>
              הגש הצעת מחיר
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    paddingTop: '100px',
    paddingBottom: '50px',
  },
  searchContainer: {
    textAlign: 'center',
    marginBottom: '30px',
  },
  searchInput: {
    width: '300px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #aaa',
    fontSize: '16px',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
    gap: '20px',
    padding: '0 40px',
  },
  card: {
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '15px',
    textAlign: 'center',
  },
  image: {
    width: '100%',
    height: '150px',
    objectFit: 'cover',
    borderRadius: '5px',
  },
  description: {
    margin: '10px 0',
    fontSize: '16px',
  },
  button: {
    backgroundColor: '#1e90ff',
    color: 'white',
    border: 'none',
    padding: '8px 12px',
    cursor: 'pointer',
    borderRadius: '4px',
  },
};

export default Home;
