import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getUser } from '../Auth';
import './Pages.css';

function ItemDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = getUser();

  const [item, setItem] = useState(null);
  const [offer, setOffer] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:8080/api/items/${id}`)
      .then(res => setItem(res.data))
      .catch(err => console.error('Error loading item:', err));
  }, [id]);

  const handleSubmitOffer = async () => {
    try {
      await axios.post(`http://localhost:8080/api/offers`, {
        amount: parseFloat(offer),
        itemId: id,
        userId: user.id,
      });
      alert('Offer submitted successfully!');
      setOffer('');
    } catch (err) {
      alert('Failed to submit offer');
      console.error(err);
    }
  };

  if (!item) return <div>טוען פרטי מוצר...</div>;

  const isOwner = item.seller?.id === user?.id;

  return (
    <div className="item-details-container" dir="rtl">
      <h2>{item.title}</h2>
      <img src={item.imageUrl} alt={item.title} className="item-img" />
      <p>{item.description}</p>
      <p>תאריך התחלה: {item.startDate}</p>
      <p>תאריך סיום: {item.endDate}</p>
      <p>מחיר פתיחה: ₪{item.startingPrice}</p>
      <p>מחיר נוכחי: ₪{item.currentPrice}</p>

      <div className="offer-section">
        <label>הצעה חדשה:</label>
        <input
          type="number"
          value={offer}
          onChange={(e) => setOffer(e.target.value)}
          className="signin-input"
        />
        <button onClick={handleSubmitOffer} className="signin-btn">
          שלח הצעה
        </button>
      </div>

      {isOwner && (
        <button
          className="signin-btn"
          style={{ marginTop: '1rem' }}
          onClick={() => navigate(`/item/${id}/offers`)}
        >
          הצג את כל ההצעות
        </button>
      )}
    </div>
  );
}

export default ItemDetails;
